mapper.insert:
INSERT INTO CT_API_META_ADDR (
	API_CD
	, API_ADDR_SN
	, API_ADDR_NM
	, API_ADDR
	, API_ADDR_CN
	, API_RQST_CN
	, USE_YN
) VALUES (
	#{apiCd}
	, #{apiAddrSn}
	, #{apiAddrNm}
	, #{apiAddr}
	, #{apiAddrCn}
	, #{apiRqstCn}
	, #{useYn}
)

mapper.update:
UPDATE CT_API_META_ADDR SET
	API_ADDR_SN = #{apiAddrSn}
	, API_ADDR_NM = #{apiAddrNm}
	, API_ADDR = #{apiAddr}
	, API_ADDR_CN = #{apiAddrCn}
	, API_RQST_CN = #{apiRqstCn}
	, USE_YN = #{useYn}
WHERE API_CD = #{apiCd} AND API_ADDR_SN = #{apiAddrSn}
