mapper.select:
select
	${columnNm}
from ${dbmsTableNm}

select
	${columnName} as col_val
from ${dbmsTableNm}

limit ${limitCount}

select 
	count(*)
from ${dbmsTableNm}
where ${whereValue}

select 
	count(*)
from
	(
	select 
		${columnNm}
	from ${dbmsTableNm}
	where trim(${columnNm}) != ''
	and ${columnNm} is not null
	) t1
${whereParam}>=#{chkDate}

select 
	count(*)
from
	(
	select 
		${columnNm}
	from ${dbmsTableNm}
	where trim(${columnNm}) != ''
	and ${columnNm} is not null
	) t1
${whereParam}<#{chkDate}

select count(*) as cnt 
from(
	select count(*) 
	from ${tblNm}
	where 1 = 1



	group by ${colNm}
	having count(*) = 1
) t1

select nvl(sum(t1.cnt), 0)
from (
	select 
		${colNm}
		, count(*) as cnt
	from
		${tblNm}
	where
		${colNm} is not null



	group by ${colNm}
	having count(*) > 1 
) t1

select count(*) as cnt 
from (
	select
		distinct ${colNm}
	from ${tblNm}
	where 1 = 1



) t1

select count(*) as cnt
from ${tblNm}
where ${colNm} is null

select 
	t1.data_value
	, t1.data_cnt 
from (
	select
		rownum as rnum
		, t01.*
	from (
		select
			 substr(${colNm}, 1, 50) as data_value
			 , count(*) as data_cnt
		from ${tblNm}
		where 1 = 1



		group by ${colNm}
		order by data_cnt desc
	) t01
) t1
where t1.rnum between 1 and 10

select 
	${colNm}
	${pkColNm}
from
	(
	select 
		*
	from (
		select
			rownum as rnum
			, t001.*
		from
			${tblNm} t001
		where 1 = 1

		and t001.${colNm} is not null




		) t01
	order by t01.rnum
	) t1
where t1.rnum between ${startRow} and ${endRow}

select ${colNm} 
${pkColNm}
from ${tblNm}
where 1 = 1

and ${colNm} is not null

select ${colNm} 
${pkColNm}
from ${tblNm}
where ${colNm} not in (
	${ruleExprsnValue}
)

select
	${colNm}
from ${tblNm}
where ${colNm} between ${bgngValue} and ${endValue}

select
	${colNm}
	${pkColNm}
from ${tblNm}
where ${colNm} not between ${bgngValue} and ${endValue}

select
	count(*)
from ${tblNm}
where ${colNm} between #{bgngValue} and #{endValue}

select
	${colNm}
	${pkColNm}
from ${tblNm}
where ${colNm} not between #{bgngValue} and #{endValue}

select
	${colNm}
from ${tblNm} 
where to_char(${colNm},'YYYY-MM-DD') between #{bgngValue} and #{endValue}

select
	${colNm}
	${pkColNm}
from ${tblNm} 
where to_char(${colNm},'YYYY-MM-DD') not between #{bgngValue} and #{endValue}
